import React, { useState } from "react";
import "../App.css";
import emailjs from "emailjs-com";

function Contact() {
  const [name, setName] = useState("");
  const [subject, setSubject] = useState("");
  const [email, setEmail] = useState("");
  const [message, setMessage] = useState("");

  const [error, setError] = useState(false);
  const [done, setDone] = useState(false);

  function sendEmail(e) {
    e.preventDefault();
  }

  const handleClick = () => {
    console.log("name", name);
    console.log("subject", subject);
    console.log("email", email);
    console.log("message", message);
    if (name !== "" && subject !== "" && email !== "" && message !== "") {
      emailjs
        .send("service_4itys1o", "template_aj2r0m8", {
          name: name,
          subject: subject,
          message: message,
          email: email,
        })
        .then((result) => {
          setDone(true);
        });
      setDone(true);
    } else {
      setError(true);
    }
  };
  return (
    <div>
      <link
        rel="stylesheet"
        href="https://stackpath.bootstrapcdn.com/bootstrap/4.5.2/css/bootstrap.min.css"
        integrity="sha384-JcKb8q3iqJ61gNV9KGb8thSsNjpSL0n8PARn9HuZOnIxN0hoP+VmmDGMN5t9UJ0Z"
        crossOrigin="anonymous"
      ></link>
      <form>
        <fieldset>
          <div className="row position">
            <div className="col">
              <input
                onChange={(e) => {
                  setError(false);
                  setDone(false);
                  setName(e.target.value);
                }}
                value={name}
                type="text"
                className="form-control"
                placeholder="Name * "
              />
            </div>
          </div>
          <div className="row position">
            <div className="col">
              <input
                onChange={(e) => {
                  setError(false);
                  setDone(false);
                  setEmail(e.target.value);
                }}
                value={email}
                type="text"
                className="form-control"
                placeholder="Email * "
              />
            </div>
          </div>
          <div className="row position">
            <div className="col">
              <input
                onChange={(e) => {
                  setError(false);
                  setDone(false);
                  setSubject(e.target.value);
                }}
                value={subject}
                type="text"
                className="form-control"
                placeholder="Subject * "
              />
            </div>
          </div>
          <div>
            <textarea
              className="Message position"
              value={message}
              onChange={(e) => {
                setError(false);
                setDone(false);
                setMessage(e.target.value);
              }}
              cols="50"
              rows="15"
              placeholder="Message * "
            ></textarea>
          </div>

          <div onClick={handleClick} className="light center">
            <div className="waves-effect waves-light btn-large">
              <i className="material-icons center">send</i>
            </div>
          </div>
          {error && (
            <div className="alert alert-danger center" role="alert">
              Please complete all fields!
            </div>
          )}
          {done && (
            <div className="alert alert-success center" role="alert">
              Done{" "}
              <span role="img" aria-label="emogis">
                👌🏽🚀
              </span>
            </div>
          )}
        </fieldset>
      </form>
    </div>
  );
}

export default Contact;
